package Day_3;

public class Non_Static_Variable {

	int x1=20;
	float x2=10.9f;
	String x3="hero";
	char x4='H';
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Non_Static_Variable n1=new Non_Static_Variable();
		System.out.println(n1.x1);
		System.out.println(n1.x2);
		System.out.println(n1.x3);
		System.out.println(n1.x4);

	}

}
